import 'package:retrofit/http.dart';import 'package:dio/dio.dart';import 'package:rickandmorty/domain/entities/character.dart';import 'package:rickandmorty/presenter/utils/constants.dart';part 'rest_client.g.dart';@RestApi(baseUrl: Constants.baseURL)abstract class RestClient {  factory RestClient(Dio dio, {String baseUrl}) = _RestClient;  @GET('/${Constants.characterEndpoint}?page={page}')  Future<CharacterResponse> getCharacters(@Path('page') page);  @GET('/${Constants.characterEndpoint}/{id}')  Future<Character> getCharacterById(@Path('id') id);}