import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import 'package:rickandmorty/data/rest_client.dart';import 'package:rickandmorty/domain/entities/character.dart';part 'character_detail_state.dart';class CharacterDetailCubit extends Cubit<CharacterDetailState> {  CharacterDetailCubit(this._client) : super(CharacterDetailLoadingState());  final RestClient _client;  Future<void> getCharacterByID(int id) async {    try {      emit(CharacterDetailLoadingState());      _client.getCharacterById(id).then((value) {        emit(CharacterDetailSuccsesLoadState(value));      });    } catch (e) {      emit(CharacterDetailErrorState(error: e));    }  }}