import 'dart:io';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class LoadingIndicator extends StatelessWidget {  const LoadingIndicator({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.all(8.0),      child: Center(        child: Platform?.isMacOS || Platform.isIOS            ? const CupertinoActivityIndicator()            : const CircularProgressIndicator(),      ),    );  }}